import React from "react";
import styled from "styled-components";
import { ReactComponent as ArrowSvg } from "../assets/arrow.svg";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

const HomePage = () => {
  return (
    <Container>
      <Row>
        <Col xs={12} md={4}>
          <DifficultyCard difficulty={0} />
        </Col>
        <Col xs={12} md={4}>
          <DifficultyCard difficulty={1} />
        </Col>
        <Col xs={12} md={4}>
          <DifficultyCard difficulty={2} />
        </Col>
      </Row>
    </Container>
  );
};

export default HomePage;

const EasyExamples = ["Two Sum", "Palindrome Number", "Merge Sorted Array"];
const MediumExamples = [
  "Reverse Integer",
  "Multiply Strings",
  "Binary Tree Upside Down",
];
const HardExamples = [
  "Travelling Salesman 2D",
  "Merge k-Sorted Lists",
  "N-Queens",
];

const DifficultyInfo = [
  {
    text: "Easy",
    emoji: "ðŸ˜Ž",
    examplesArr: EasyExamples,
    bg: "#f0fdf4",
    textColor: "var(--green)",
  },
  {
    text: "Medium",
    emoji: "ðŸ˜¥",
    examplesArr: MediumExamples,
    bg: "#FFFBEB",
    textColor: "#D97706",
  },
  {
    text: "Hard",
    emoji: "ðŸ¥µ",
    examplesArr: HardExamples,
    bg: "#FFF1F2",
    textColor: "#e11d48",
  },
];

const DifficultyCard = ({ difficulty }) => {
  return (
    <StyledDifficultyCard
      difficulty={difficulty}
      style={{ backgroundColor: `${DifficultyInfo[difficulty].bg}` }}
    >
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          marginBottom: "32px",
        }}
      >
        <h1 style={{ color: `${DifficultyInfo[difficulty].textColor}` }}>
          {DifficultyInfo[difficulty].text}
        </h1>
        <h1>{DifficultyInfo[difficulty].emoji}</h1>
      </div>
      <div style={{ marginBottom: "60px" }}>
        {DifficultyInfo[difficulty].examplesArr.map((example) => {
          return <p>{example}</p>;
        })}
        <p>...and more!</p>
      </div>
      <p>
        <span
          style={{
            marginRight: "20px",
            color: `${DifficultyInfo[difficulty].textColor}`,
          }}
        >
          Join waiting room
        </span>
        <ArrowSvg />
      </p>
    </StyledDifficultyCard>
  );
};

const StyledDifficultyCard = styled.div`
  padding: 48px;
  /* width: 100%; */
  border-radius: 20px;

  svg path {
    ${({ difficulty }) =>
      (difficulty === 0 &&
        `fill: var(--green);
    stroke: var(--green);
  `) ||
      (difficulty === 1 &&
        `fill: #D97706;
    stroke: #D97706;
  `) ||
      (difficulty === 2 &&
        `fill: #e11d48;
    stroke: #e11d48;
  `)}
  }

  h1 {
    font-size: 40px;
    font-weight: 800;
    text-transform: capitalize;
  }

  p {
    color: var(--base-700);
    font-size: 18px;
  }

  @media (max-width: 640px) {
    padding: 32px;
  }
`;
