import React from "react";
import styled from "styled-components";
import { ReactComponent as ArrowSvg } from "../assets/arrow.svg";

const HomePage = () => {
  return (
    <div>
      <DifficultyCard difficulty={0} />
      <DifficultyCard difficulty={1} />
      <DifficultyCard difficulty={2} />
    </div>
  );
};

export default HomePage;

const colors = [
  { bg: "#f0fdf4", text: "var(--green)" },
  { bg: "#FFFBEB", text: "#D97706" },
];

const DifficultyCard = ({ difficulty }) => {
  return (
    <StyledDifficultyCard
      style={{ backgroundColor: `${colors[difficulty].bg}` }}
    >
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          marginBottom: "32px",
        }}
      >
        <h1 style={{ color: `${colors[difficulty.text]}` }}>{difficulty}</h1>
        <h1>ðŸ˜Ž</h1>
      </div>
      <div style={{ marginBottom: "60px" }}>
        <p>Two Sum</p>
        <p>Palindrome Number</p>
        <p>Merge Sorted Array</p>
        <p>...and more!</p>
      </div>
      <p>
        <span style={{ marginRight: "20px" }}>Join waiting room</span>
        <StyledArrowSvg
          bg={colors[difficulty].bg}
          text={colors[difficulty.text]}
        >
          <ArrowSvg />
        </StyledArrowSvg>
      </p>
    </StyledDifficultyCard>
  );
};

const StyledDifficultyCard = styled.div`
  padding: 48px;
  width: 250px;
  border-radius: 20px;
  /* ${({ difficulty }) =>
    (difficulty === "easy" &&
      `background-color: #f0fdf4;
    color: var(--green);
  `) ||
    (difficulty === "medium" &&
      `background-color: #FFFBEB;
    color: #D97706;
  `) ||
    (difficulty === "hard" &&
      `background-color: #FFF1F2;
    color: #e11d48;
  `)} */

  /* svg path {
    fill: blue;
    stroke: blue;
  } */

  h1 {
    font-size: 40px;
    font-weight: 800;
    text-transform: capitalize;
  }

  p {
    color: var(--base-700);
    font-size: 18px;
  }

  @media (max-width: 640px) {
    padding: 32px;
  }
`;

const StyledArrowSvg = styled.div`
  svg path {
    ${({ text, bg }) =>
      `background-color: #f0fdf4;
    color: var(--green);
  `};
  }
`;
