import React from "react";
import styled from "styled-components";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import heroImage from "../assets/heroImage.png";

const LandingPage = () => {
  return (
    <StyledHeroSection>
      <div className="hero-container">
        <Row>
          <Col xs={12} md={8}>
            <h1>Lorem ipsum dolor amet lorem</h1>
            <Row>
              <PointBlock
                title="Lorem ipsum dolor"
                description="Instantly meet others that are of a similar skill level as you are."
              />
              <PointBlock
                title="Lorem ipsum dolor"
                description="Instantly meet others that are of a similar skill level as you are."
              />
              <PointBlock
                title="Lorem ipsum dolor"
                description="Instantly meet others that are of a similar skill level as you are."
              />
            </Row>
          </Col>
          <Col xs={12} md={4}>
            <div className="image-wrap">
              <img src={heroImage} />
            </div>
          </Col>
        </Row>
      </div>
    </StyledHeroSection>
  );
};

export default LandingPage;

const PointBlock = ({ title, description }) => {
  return (
    <Col xs={12} sm={4}>
      <StyledPointBlock className="my-3 my-sm-0">
        <h2>{title}</h2>
        <p>{description}</p>
      </StyledPointBlock>
    </Col>
  );
};

const StyledPointBlock = styled.div`
  display: flex;
  flex-direction: column;

  h2 {
    font-size: var(--text-lg);
    font-weight: 600;
    margin-bottom: 12px;
  }

  p {
    color: var(--base-600);
    line-height: 150%;
  }

  @media (max-width: 576px) {
    h2 {
      font-size: var(--text-md);
    }
  }
`;

const StyledHeroSection = styled.div`
  position: relative;
  overflow: hidden;
  padding-top: 96px;
  padding-bottom: 96px;

  .col-md-8 {
    min-height: 464px;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
  }
  .col-md-4 {
    min-height: 464px;
    display: flex;
    align-items: center;
  }

  h1 {
    font-size: 64px;
    font-weight: 800;
    margin-bottom: 40px;
  }

  .hero-container {
    z-index: 9998;
    width: 100%;
    max-width: 1256px;
    margin-right: auto;
    margin-left: auto;
    padding-right: 20px;
    padding-left: 20px;
  }

  .image-wrap {
    position: absolute;
    overflow: hidden;
    width: 1022px;
    height: 440px;

    @media (max-width: 767px) {
      position: static;
      width: auto;
      height: auto;
      margin-top: 32px;
    }
  }

  img {
    position: absolute;
    left: 48px;
    top: 0;
    right: 0;
    bottom: 0;
    display: block;
    height: 100%;
    -o-object-fit: cover;
    object-fit: cover;

    @media (max-width: 767px) {
      position: static;
      width: 100%;
    }
  }
`;
