import React from "react";
import styled from "styled-components";
import { ReactComponent as ArrowSvg } from "../assets/arrow.svg";

const HomePage = () => {
  return (
    <div>
      <DifficultyCard difficulty="easy" />
    </div>
  );
};

export default HomePage;

const DifficultyCard = ({ difficulty }) => {
  return (
    <StyledDifficultyCard difficulty={difficulty}>
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          marginBottom: "32px",
        }}
      >
        <h1 className={`${difficulty}`}>{difficulty}</h1>
        <h1>ðŸ˜Ž</h1>
      </div>
      <div style={{ marginBottom: "60px" }}>
        <p>Two Sum</p>
        <p>Palindrome Number</p>
        <p>Merge Sorted Array</p>
        <p>...and more!</p>
      </div>
      <p>
        <span style={{ marginLeft: "20px" }}>Join waiting room</span>
        <ArrowSvg />
      </p>
    </StyledDifficultyCard>
  );
};

const StyledDifficultyCard = styled.div`
  padding: 48px;
  width: 250px;
  border-radius: 20px;

  ${({ difficulty }) =>
    difficulty == "easy" &&
    `
    background-color: #f0fdf4;
  `}

  .easy {
    color: var(--green);
  }

  .medium {
    color: var(--yellow);
  }

  .hard {
    color: #e11d48;
  }

  h1 {
    font-size: 40px;
    font-weight: 800;
    text-transform: capitalize;
  }

  p {
    font-size: 18px;
    color: var(--base-700);
  }

  @media (max-width: 640px) {
    padding: 32px;
  }
`;
