import React from "react";
import styled from "styled-components";
import { ReactComponent as ArrowSvg } from "../assets/arrow.svg";

const HomePage = () => {
  return (
    <div>
      <DifficultyCard difficulty="easy" />
      <DifficultyCard difficulty="medium" />
      <DifficultyCard difficulty="hard" />
    </div>
  );
};

export default HomePage;

const colors = [
  { bg: "#f0fdf4", text: "var(--green)" },
  { bg: "#FFFBEB", text: "#D97706" },
];

const DifficultyCard = ({ difficulty }) => {
  console.log(Object.values(colors));
  return (
    <StyledDifficultyCard difficulty={difficulty}>
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          marginBottom: "32px",
        }}
      >
        <h1 className={`${difficulty}`}>{difficulty}</h1>
        <h1>ðŸ˜Ž</h1>
      </div>
      <div style={{ marginBottom: "60px" }}>
        <p>Two Sum</p>
        <p>Palindrome Number</p>
        <p>Merge Sorted Array</p>
        <p>...and more!</p>
      </div>
      <p>
        <span style={{ marginRight: "20px" }}>Join waiting room</span>
        <ArrowSvg />
      </p>
    </StyledDifficultyCard>
  );
};

const StyledDifficultyCard = styled.div`
  padding: 48px;
  width: 250px;
  border-radius: 20px;

  ${({ difficulty }) =>
    (difficulty === "easy" &&
      `background-color: #f0fdf4;
    color: var(--green);
  `) ||
    (difficulty === "medium" &&
      `background-color: #FFFBEB;
    color: #D97706;
  `) ||
    (difficulty === "hard" &&
      `background-color: #FFF1F2;
    color: #e11d48;
  `)}

  svg path {
    fill: blue;
    stroke: blue;
  }

  h1 {
    font-size: 40px;
    font-weight: 800;
    text-transform: capitalize;
  }

  p {
    font-size: 18px;
  }

  @media (max-width: 640px) {
    padding: 32px;
  }
`;
